describe('.only is messed up', () => {
  def('thing', () => void 0)

  context.only('when thing is overridden', () => {
    def('thing', () => 1)

    it('works okay', () => expect(get('thing')).to.equal(1))
  })

  context('another context', () => {
    def('thing', () => 2)

    it('works okay', () => expect(get('thing')).to.equal(2))
  })
})
